mapper.select:
    SELECT 
    
    FROM plat_user
    
    	 AND  USER_ID = #{userId}
		AND  LOGIN_NAME = #{loginName}
		AND  PASSWORD = #{password}
		AND  STATUS = #{status}
		AND  USER_NAME = #{userName}
		AND  MOBILE = #{mobile}
		AND  EMAIL = #{email}
		AND  USER_TYPE = #{userType}
		AND  ACCOUNT_EXPIRE_DATE = #{accountExpireDate}
		AND  PWD_EXPIRE_DATE = #{pwdExpireDate}
		AND  IS_LOCKED = #{isLocked}
		AND  REC_CREATOR = #{recCreator}
		AND  REC_CREATE_TIME = #{recCreateTime}
		AND  REC_REVISOR = #{recRevisor}
		AND  REC_REVISE_TIME = #{recReviseTime}

    SELECT 
    
    FROM plat_user
    
