		AND  ACCOUNT_EXPIRE_DATE = #{accountExpireDate}
		AND  PWD_EXPIRE_DATE = #{pwdExpireDate}
		AND  IS_LOCKED = #{isLocked}
		AND  REC_CREATOR = #{recCreator}
