		AND  REC_REVISOR = #{recRevisor}
		AND  REC_REVISE_TIME = #{recReviseTime}

mapper.update:
UPDATE plat_user
USER_ID = #{userId}, LOGIN_NAME = #{loginName}, PASSWORD = #{password}, STATUS = #{status}, USER_NAME = #{userName}, MOBILE = #{mobile}, EMAIL = #{email}, USER_TYPE = #{userType}, ACCOUNT_EXPIRE_DATE = #{accountExpireDate}, PWD_EXPIRE_DATE = #{pwdExpireDate}, IS_LOCKED = #{isLocked}, REC_CREATOR = #{recCreator}, REC_CREATE_TIME = #{recCreateTime}, REC_REVISOR = #{recRevisor}, REC_REVISE_TIME = #{recReviseTime},
WHERE USER_ID=#{userId}

